"use strict";

module.exports = {
  languages: [
    {
      name: "foo",
      parsers: ["foo"],
      extensions: [".foo"]
    }
  ],
  parsers: {
    foo: {
      parse: text => ({ text }),
      astFormat: "foo"
    }
  },
  printers: {
    foo: {
      async print(path) {
        const prettier = await import("../../../config/prettier-entry.js");
        const { concat } = prettier.doc.builders;
        return concat(["foo+", path.getValue().text]);
      }
    }
  }
};
