"use strict";

module.exports = {
  languages: [
    {
      name: "bespoke",
      parsers: ["bespoke"]
    }
  ],
  parsers: {
    bespoke: {
      parse: text => ({ text }),
      astFormat: "bespoke"
    }
  },
  printers: {
    bespoke: {
      async print(path) {
        const {default: prettier}= await import("../../../../../../config/prettier-entry.js");
        const { concat } = prettier.doc.builders;
        return concat(["bespoke+", path.getValue().text]);
      },
    }
  }
};
